/**
 * Bank Card Component
 * Creates an interactive bank card that can be included in any page
 */
import { createElement } from '../utils/dom.js';

class BankCard {
    constructor(options = {}) {
        const {
            cardNumber = '9759 2484 5269 6576',
            name = 'CARDHOLDER NAME',
            validThru = '12/24',
            cvv = '***',
            status = 'default', // default, verified, error, processing, detecting
            container = null
        } = options;
        
        this.cardNumber = cardNumber;
        this.name = name;
        this.validThru = validThru;
        this.cvv = cvv;
        this.status = status;
        this.container = container;
        
        this.element = this.createCardElement();
        
        if (this.container) {
            this.render(this.container);
        }
    }
    
    createCardElement() {
        // Create the flip card container
        const flipCard = createElement('div', {
            className: `flip-card ${this.status}`
        });
        
        // Create the inner container that will flip
        const flipCardInner = createElement('div', {
            className: 'flip-card-inner'
        });
        
        // Create the front of the card
        const flipCardFront = createElement('div', {
            className: 'flip-card-front'
        });
        
        // Add elements to the front of the card
        flipCardFront.innerHTML = `
            <p class="heading_8264">MASTERCARD</p>
            <svg class="logo" xmlns="http://www.w3.org/2000/svg" x="0px" y="0px" width="36" height="36" viewBox="0 0 48 48">
                <path fill="#ff9800" d="M32 10A14 14 0 1 0 32 38A14 14 0 1 0 32 10Z"></path>
                <path fill="#d50000" d="M16 10A14 14 0 1 0 16 38A14 14 0 1 0 16 10Z"></path>
                <path fill="#ff3d00" d="M18,24c0,4.755,2.376,8.95,6,11.48c3.624-2.53,6-6.725,6-11.48s-2.376-8.95-6-11.48 C20.376,15.05,18,19.245,18,24z"></path>
            </svg>
            <svg version="1.1" class="chip" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" width="30px" height="30px" viewBox="0 0 50 50" xml:space="preserve">
                <image id="image0" width="50" height="50" x="0" y="0" href="data:image/png;base64,  amiEqJKM+X1gxvWXpoUjVIVPnwErw71nmpgiqiQGBjNzbgs3j1nus+fMndc+Cwm0T52/oNR9lsdCTMTE0mb8GRAEyf8qZ+KYvfMWDKAAROUuSFImomo9GKRN7iBWN0j++/cf0n8AAD2IZzDTlXsAAAAASUVORK5CYII="></image>
            </svg>
            <svg version="1.1" class="contactless" id="Layer_1" xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" x="0px" y="0px" width="20px" height="20px" viewBox="0 0 50 50" xml:space="preserve">
                <image id="image0" width="50" height="50" x="0" y="0" href="data:image/png;base64, "></image>
            </svg>
            <p class="number">${this.cardNumber}</p>
            <p class="valid_thru">VALID THRU</p>
            <p class="date_8264">${this.validThru}</p>
            <p class="name">${this.name}</p>
        `;
        
        // Create the back of the card
        const flipCardBack = createElement('div', {
            className: 'flip-card-back'
        });
        
        // Add elements to the back of the card
        flipCardBack.innerHTML = `
            <div class="strip"></div>
            <div class="mstrip"></div>
            <div class="sstrip">
              <p class="code">${this.cvv}</p>
            </div>
        `;
        
        // Assemble the card
        flipCardInner.appendChild(flipCardFront);
        flipCardInner.appendChild(flipCardBack);
        flipCard.appendChild(flipCardInner);
        
        return flipCard;
    }
    
    render(container) {
        if (typeof container === 'string') {
            container = document.querySelector(container);
        }
        
        if (container) {
            container.appendChild(this.element);
        }
        
        return this.element;
    }
    
    setStatus(status) {
        // Remove all status classes
        this.element.classList.remove('default', 'verified', 'error', 'processing', 'detecting');
        
        // Add the new status class
        this.element.classList.add(status);
        this.status = status;
    }
    
    setCardInfo(cardInfo = {}) {
        const { cardNumber, name, validThru, cvv } = cardInfo;
        
        // Update internal properties
        if (cardNumber) this.cardNumber = cardNumber;
        if (name) this.name = name;
        if (validThru) this.validThru = validThru;
        if (cvv) this.cvv = cvv;
        
        // Re-render the card with new info
        const newElement = this.createCardElement();
        this.element.parentNode.replaceChild(newElement, this.element);
        this.element = newElement;
    }
    
    startDetectingAnimation() {
        this.setStatus('detecting');
    }
    
    stopDetectingAnimation() {
        this.setStatus(this.status === 'detecting' ? 'default' : this.status);
    }
}

export default BankCard;